import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';



function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles(theme => ({
  button: {
    backgroundColor: '#f29648',
    textTransform: 'Capitalize',
    fontFamily: 'Open Sans',
    fontSize: '.7em',
  },
  input: {
    display: 'none',
  },
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    outline: 'none',
  },
}));

export default function CreateBooking() {
  const [open, setOpen] = React.useState(false);
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  
  const classes = useStyles();

  return (
    <div>
    <Typography gutterBottom>Click to get the full Modal experience!</Typography>
    <Button variant="contained" onClick={handleOpen} className={classes.button}>Create Booking</Button>
    <Modal
      aria-labelledby="simple-modal-title"
      aria-describedby="simple-modal-description"
      open={open}
      onClose={handleClose}
    >
      <div style={modalStyle} className={classes.paper}>
        <Typography variant="h6" id="modal-title">
          Create Booking
        </Typography>

      
        <Button variant="contained" onClick={handleClose} className={classes.button}>Create Booking</Button>
      </div>
    </Modal>
  </div>
  );
}
