import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import './App.css';

const useStyles = makeStyles(theme => ({
    container: {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
    },
    left: {
      width: 200,
      display: 'flex',
      flexDirection: 'column',
    },
    right: {
      width: 200,
      display: 'flex',
      flexDirection: 'column',
    },
    flexRow: {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
    },
    flexCol:{
      display: 'flex',
      flexDirection: 'column',
      marginBottom: 5,
    },
    button: {
      backgroundColor: '#f29648',
      textTransform: 'none',
      fontFamily: 'Open Sans',
      fontSize: '.7em',
    },
    input: {
      display: 'none',
    },
  }));

class BookingForm extends Component {
    constructor(props){
        super(props)
        this.state = {
            name: '',
            email: '',
            street: '',
            city: '',
            state: '',
            zip: '',
            bookingType: '',
            date: '',
            time: ''
        }
    }

  changeHandler = (e) => {
      this.setState({[e.target.name]: e.target.value});
  };

  submitHandler = (e) => {
      e.preventDefault();
      console.log(this.state);
  };

render(){
    const classes = useStyles();

    const {
        name,
        email,
        street,
        city,
        state,
        zip,
        bookingType,
        date,
        time } = this.state;

    return(
        <form id="booking-form" onSubmit={this.submitHandler} noValidate>
            <div className="form-left">
                <div className="form-flexCol">
                    <label>Name</label>
                    <input type="text" name="name" value={name} onChange={this.changeHandler} required />
                </div>
                <div className="form-flexCol">
                    <label>Email</label>
                    <input type="email" name="email" value={email} onChange={this.changeHandler} required />
                </div>
                <div className="form-flexCol"> 
                    <label>Street Address</label>
                    <input type="text" name="street" value={street} onChange={this.changeHandler} required />
                </div>
                <div className="form-flexCol">
                    <label>City</label>
                    <input type="text" name="city" value={city} onChange={this.changeHandler} required />
                </div>
                <div className="form-flexRow">
                    <div id="form-half-col" className="form-flexCol">
                        <label>State</label>
                        <input type="text" name="state" value={state} onChange={this.changeHandler} required />
                    </div>
                    <div id="form-half-col" className="form-flexCol">
                        <label>Zip code</label>
                        <input type="text" name="zip" value={zip} onChange={this.changeHandler} required />
                    </div>
                </div>
            </div>
            <div className="form-right">
                <div className="form-flexCol">
                <label>Booking type</label>
                <select name = "bookingType" value={bookingType} onChange={this.changeHandler}>
                    <option value="dogWalk">Dog Walk</option>
                    <option value="housekeeping">Housekeeping</option>
                </select>
                </div>
                <div className="form-flexCol">
                    <label>Booking Date</label>
                    <input type="date" name="date" value={date} onChange={this.changeHandler}/>
                </div>
                <div className="form-flexCol">
                    <label>Booking Time</label>
                    <input type="time" name="time" value={time} onChange={this.changeHandler}/>
                </div>
                <Button id="submit-booking" type="submit" variant="contained">Create booking</Button>
            </div>
            {/* onClick={handleClose} */}
        </form>
    )
    }
};

export default BookingForm;
