import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import './App.css';



function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles(theme => ({
  flexRow: {
    display: 'flex',
    flexDirection: 'row',
    marginRight: '10px',
    // flexWrap: 'wrap',
  },
  flexCol:{
    display: 'flex',
    flexDirection: 'column',
    paddingBottom: '5px',
  },
  button: {
    backgroundColor: '#f29648',
    textTransform: 'Capitalize',
    fontFamily: 'Open Sans',
    fontSize: '.7em',
  },
  input: {
    display: 'none',
  },
  paper: {
    position: 'absolute',
    width: 550,
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(4),
    outline: 'none',
  },
}));

export default function CreateBooking() {
  const [open, setOpen] = React.useState(false);
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  
  const classes = useStyles();

  return (
    <div>
    <Typography gutterBottom>Click to get the full Modal experience!</Typography>
    <Button variant="contained" onClick={handleOpen} className={classes.button}>Create Booking</Button>
    <Modal
      aria-labelledby="simple-modal-title"
      aria-describedby="simple-modal-description"
      open={open}
      onClose={handleClose}
    >
      <div style={modalStyle} className={classes.paper}>
        <Typography variant="h6" id="modal-title">
          Create Booking
        </Typography>
        <form id="booking-form" className={classes.flexRow} noValidate autoComplete="off">
          <div>
            <div className={classes.flexCol}>
              <label>Name</label>
              <input type="text" name="name" required />
            </div>
            <div className={classes.flexCol}>
              <label>Email</label>
              <input type="email" name="email" required />
            </div>
            <div className={classes.flexCol}> 
              <label>Street Address</label>
              <input type="text" name="street" required />
            </div>
            <div className={classes.flexCol}>
              <label>City</label>
              <input type="text" name="city" required />
            </div>
            <div className={classes.flexRow}>
              <div className={classes.flexCol}>
                <label>State</label>
                <input type="text" name="state" required />
              </div>
              <div className={classes.flexCol}>
                <label>Zip code</label>
                <input type="text" name="zip" required />
              </div>
            </div>
          </div>
          <div>
          <div>
            <div className={classes.flexCol}>
              <label>Booking Type</label>
              <select>
                <option value="dogWalk">Dog Walk</option>
                <option value="housekeeping">Housekeeping</option>
              </select>
            </div>
            <div className={classes.flexCol}>
              <label>Booking Date</label>
              <input type="text" name="date" placeholder="mm/dd/yyyy" />
            </div>
            <div className={classes.flexCol}>
              <label>Booking Time</label>
              <input type="text" name="time" placeholder="hh:mm am/pm"/>
            </div>
          </div>
            <Button type="submit" variant="contained" onClick={handleClose} className={classes.button}>Create Booking</Button>
          </div>
        </form>
      </div>
    </Modal>
  </div>
  );
}
